import $ from 'jquery';

var isOpen = false;
export function burgerClick(){
  var phoneBarMenu = $('#phoneBarMenu')[0];
  $('#burger')[0].addEventListener('click', ()=>{
    //$('#phoneBarMenu').css('height', 'auto');
    if(!isOpen){
      phoneBarMenu.style.height = `${phoneBarMenu.scrollHeight}px`;
      isOpen = true;
    }else{
      phoneBarMenu.style.height = 0;
      isOpen = false;
    }
    
    
  })
}

export function windowListener(){
  menuChange();
    window.addEventListener('resize', ()=>{
      menuChange();
  })
}

function menuChange(){
  if (/Mobi|Android/i.test(navigator.userAgent)) {
    $('#compBar').css('visibility', 'hidden');
    $('#phoneBar').css('visibility', 'visible');
  } else {
    $('#compBar').css('visibility', 'visible');
    $('#phoneBar').css('visibility', 'hidden');
  }
}


