import React from 'react';
import { useState } from 'react';
import axios from "axios";
import $ from 'jquery';

function LoginTabContent(){
  const [user, setUser] = useState(null);
  
  const handleLogin = (event) =>{
    event.preventDefault();
    const userNameVal = document.getElementById('usernamelogin').value;
    const passWordVal = document.getElementById('passwordlogin').value;
    
    if(userNameVal != "" && passWordVal != ""){
    axios.get(`http://localhost:8080/dnu180dn4d3d/${userNameVal}`)
    .then(response => {
      if(response.data.passWord != passWordVal){
        alert("Log in failed.");
      }else{
        alert("Log in successful!");
        setUser(response.data);
        document.location.reload();
      }
    })
    .catch(error => {
      alert("Log in failed.");
    });
  }
  };

return <>
<h2>Log In</h2>     
<div className='line col-lg-6 col-8 container-fluid mt-3 mb-4'></div>
  <div className='container-fluid col-lg-8 col-12'>
  <form onSubmit={handleLogin}>
  <div className="form-group">
    <label htmlFor="username">Username</label>
    <input type="text" className="form-control mt-2" id="usernamelogin" name="username" />
  </div>
  <div className="form-group mt-4">
    <label htmlFor="password">Password</label>
    <input type="password" className="form-control mt-2" id="passwordlogin" name="password" />
  </div>
  <button type="submit" className="btn btn-primary mt-4">Login</button>
</form>
</div>
</>;
}
function SignUpTabContent(){

  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    userName: '',
    eMail: '',
    passWord: '',
    phoneNumber: '',
    dateOfBirth: null
  });

  const handleChange = (event) => {
    const { id, value } = event.target;
  
    if (id === 'dateOfBirth') {

      const date = new Date(value).toISOString();
    setFormData({
      ...formData,
      [id]: date
      });
    } else {
      setFormData({
        ...formData,
        [id]: value
      });
    
  };}

  const handleSubmit = (event) => {
    
    var fieldsFilled = $('#firstName').val() != "" && 
    $('#lastName').val() != "" && 
    $('#dateOfBirth').val() != "" && 
    $('#userName').val() != "" && 
    $('#eMail').val() != "" &&
    $('#phoneNumber').val() != "" &&
    $('#passWord').val() != "";

    event.preventDefault();
    if(!fieldsFilled){
      alert("All fields must be filled.");
    } else if($('#passWord').val() != $('#password2').val()){
      alert("Passwords don't match.");
    } else{
      axios.get(`http://localhost:8080/dnu180dn4d3d/${formData.userName}`)
      .then(response => {
        if(response.data.message === 'User not found.'){
          axios.post('http://localhost:8080/dnu180dn4d3d', formData)
            .catch(error => console.error(error));
            alert("User created!");
          window.location.reload();
        } else {
          alert("Username taken.");
        }
      })
      .catch(error => {
        alert("Cannot create user.");
      });
    }
  };

return <>
<h2>Create Account</h2>     
<div className='line col-lg-6 col-8 container-fluid mt-3 mb-4'></div>
        <div className='container-fluid col-12 p-0'>

        <form onSubmit={handleSubmit}> 
        
        <h3 className='text-lg-start text-sm-center container-fluid col-11 mt-5 mb-4 p-0'>Personal details</h3>
        {/* <div className="row">
            <div className="form-group col-lg-6 text-start">
            <label htmlFor="firstname">First Name</label>
            </div>
            <div className="form-group col-lg-6 text-start">
            <label htmlFor="lastname">Last Name</label>
            </div>
        </div>
        <div className="row">
            <div className="form-group col-md-6">
            <input type="text" className="form-control mt-2" id="firstName" defaultValue={formData.firstName} onChange={handleChange}/>
            </div>
            <div className="form-group col-md-6">
            <input type="text" className="form-control mt-2" id="lastName" defaultValue={formData.lastName} onChange={handleChange}/>
            </div>
        </div> */}
        
        <div className='container-fluid col-12'>
            <div className='row'> 
              <div className='col-lg-6 col-12 text-start'>
                <label htmlFor="firstname">First Name</label>
                <input type="text" className="form-control mt-2" id="firstName" defaultValue={formData.firstName} onChange={handleChange}/>
              </div>
              <div className='col-lg-6 col-12 text-start mt-lg-0 mt-2'>
                <label htmlFor="lastname">Last Name</label>
                <input type="text" className="form-control mt-2" id="lastName" defaultValue={formData.lastName} onChange={handleChange}/>
              </div>
            </div>
          </div>

          <div className='container-fluid col-12 mt-lg-4 mt-2'>
            <div className='row'>
