let content;
export const SwitchContent = (page) => {
  if (page === 'create') {
    setCurrentPage('sign up');
  } else {
    setCurrentPage('login');
  }
};

export const setCurrentPage = (page) =>{
  const [currentPage, setCurrentPage] = useState('');
  setCurrentPage(page);
  switch (currentPage) {
    case 'login':
      content = <LoginTabContent />;
      break;
    case 'sign up':
      content = <SignUpTabContent />;
      break;
    default:
      content = null;
  }
}


function LoginPage() {
  
    return (
        <div className='container-fluid'>
          <div className='row'>
          <div className='tabContainer justify-content-between col-12'>
                <div className='row justify-content-between'>
                <button className='tab col-4' onClick={() => setCurrentPage('login')}>Log In</button>
                <button className='tab col-8' onClick={() => setCurrentPage('sign up')}>Create Account</button>
                </div>
              </div>
            <div className='col-lg-6 col-11 container-fluid mainPage mb-5'>
            <div>{content}</div>
            </div>
          </div>
        </div>
      );
  }

  

  export default LoginPage;
